interface IconProps {
  name: string;
  size?: number;
  className?: string;
  stroke?: string;
  isWishlist?: boolean;
  strokeWidth?: string | number;
  fill?: string;
  viewBox?: string;
}

const Icon = ({
  name,
  size = 24,
  className = "",
  stroke = "currentColor",
  isWishlist,
  strokeWidth = "1",
  fill = "none",
  viewBox = "0 0 24 24",
}: IconProps) => {
  const iconContent = icons[name];

  if (!iconContent) {
    return null;
  }

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      width={size}
      height={size}
      fill={fill}
      stroke={stroke}
      className={className}
      strokeWidth={strokeWidth}
      viewBox={viewBox}
    >
      {typeof iconContent === "function"
        ? iconContent(isWishlist)
        : iconContent}
    </svg>
  );
};

export default Icon;

